mapper.update:
UPDATE GRADE
   SET ATT_SCORE = #{ attScore },
       ASS_SCORE = #{ assScore },
       MID_SCORE = #{ midScore },
       FIN_SCORE = #{ finScore }
 WHERE STUDENT_NO = #{ studentNo }
   AND LECTURE_NO = #{ lectureNo }

mapper.delete:
DELETE FROM GRADE
 WHERE PROF_NO = #{ profNo }
   AND STUDENT_NO = #{ studentNo }
   AND LECTURE_NO = #{ lectureNo }
