mapper.update:
UPDATE STUDENT
   SET STUDENT_PWD = #{ studentPwd },
   	   ADDRESS = #{ address },
   	   STUDENT_TELNO = #{ studentTelNo },
	   EMAIL = #{ email }
 WHERE STUDENT_NO = #{ studentNo }

mapper.delete:
DELETE
  FROM STUDENT
 WHERE STUDENT_NO = #{ studentNo }

DELETE 
FROM ENROLLMENT
WHERE 1 = 1
AND STUDENT_NO = #{ studentNo } 
AND LECTURE_NO = #{ lectureNo }

mapper.select:
SELECT A.LECTURE_NAME, A.PROF_NAME, A.STUDENT_NO, A.STUDENT_NAME, A.ATT_SCORE, A.ASS_SCORE, A.MID_SCORE, A.FIN_SCORE, A.GRADE FROM VIEW_GRADE_CHECK A JOIN GRADE B ON A.STUDENT_NO = B.STUDENT_NO WHERE A.STUDENT_NO = #{ studentNo }
AND B.PROF_NO = #{ profNo }
AND B.LECTURE_NO = #{ lectureNo }

mapper.update:
UPDATE GRADE A
   SET A.GRADE = #{	grade }
 WHERE A.STUDENT_NO = #{ studentNo }
   AND A.LECTURE_NO = #{ lectureNo }
   AND A.PROF_NO = #{ profNo }

UPDATE GRADE
SET ATT_SCORE = #{ attScore },
ASS_SCORE = #{ assScore },
MID_SCORE = #{ midScore },
FIN_SCORE = #{ finScore }
WHERE STUDENT_NO = #{ studentNo }
AND LECTURE_NO = #{ lectureNo }

mapper.delete:
DELETE FROM GRADE
WHERE PROF_NO = #{ profNo }
AND STUDENT_NO = #{ studentNo }
AND LECTURE_NO = #{ lectureNo }

mapper.update:
UPDATE LECTURE_JUG
SET STU_JUG_SCORE= #{ stuJugScore },
STU_ONE_JUG = #{ stuOneJug }
WHERE STUDENT_NO = #{ studentNo }
AND JUDGEMENT_NO = #{ judgementNo }

mapper.delete:
DELETE FROM LECTURE_JUG
WHERE  JUDGEMENT_NO = #{ judgementNo }
AND STUDENT_NO = #{ studentNo }
